from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import ColorSensor, Motor, UltrasonicSensor
from pybricks.robotics import DriveBase

# Set up all devices.
prime_hub = PrimeHub()
color_sensor = ColorSensor(Port.D)
distance_sensor = UltrasonicSensor(Port.C)
LM = Motor(Port.B, Direction.COUNTERCLOCKWISE)
RM = Motor(Port.F, Direction.CLOCKWISE)
LAD = Motor(Port.A, Direction.COUNTERCLOCKWISE)
RAD = Motor(Port.E, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(LM, RM, 62, 97)


# The main program starts here.
print(prime_hub.imu.tilt()[0])
drive_base.settings(straight_speed=600)
drive_base.use_gyro(True)
drive_base.straight(500)
drive_base.settings(turn_rate=90)
drive_base.turn(-90)
RAD.run_angle(500, 360)
print(prime_hub.imu.tilt()[0])
drive_base.straight(150)
drive_base.turn(90)
print(distance_sensor.distance())
drive_base.settings(straight_speed=-250)
while distance_sensor.distance() >= 50:
    drive_base.drive(-250, 0)
    print(distance_sensor.distance())
drive_base.straight(0)
